body {
  --lightMain: lightgray;
  --lightSecond: white;
  --lightAccent: rgb(18, 182, 236);
  transition: 1s all;
}

body.dark {
  --lightMain: #150050;
  --lightSecond: #3F0071;
  --lightAccent: #610094;
}

body, html {
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, "Open Sans", "Helvetica Neue", sans-serif;
  padding: 0;
  margin: 0;
  width: 100%;
  height: 100%;
  background-color: var(--lightMain);
}

.themetogglebutton {
  background-color: var(--lightSecond);
  border: none;
}

.themetogglebutton:hover {
  cursor: pointer;
}

.w-4 {
  width: 1rem;
}

.h-4 {
  height: 1rem;
}

.w-6 {
  width: 1.5rem;
}

.h-6 {
  height: 1.5rem;
}

.w-8 {
  width: 2rem;
}

.h-8 {
  height: 2rem;
}

.popup-box {
  position: fixed;
  background-color: #00000050;
  width: 100%;
  height: 100vh;
  top: 0;
  left: 0;
}
.popup-box .box {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  width: 33%;
  margin: 0 auto;
  height: auto;
  max-height: 70vh;
  margin-top: 15rem;
  background: #fff;
  border-radius: 1rem;
  padding: 20px;
  border: 1px solid #999;
  overflow: auto;
}

.footer {
  display: flex;
  flex-direction: column;
}
.footer .up-footer {
  display: flex;
  justify-content: space-evenly;
  margin-left: 25rem;
  margin-right: 25rem;
  font-size: smaller;
  color: gray;
}
.footer .up-footer div a {
  text-decoration: none;
  color: gray;
}
.footer .bot-footer {
  padding-top: 2rem;
  padding-bottom: 3rem;
  display: flex;
  justify-content: center;
  font-size: smaller;
  color: gray;
}
.footer .bot-footer .text {
  padding-left: 1rem;
}
.footer .bot-footer select {
  background-color: var(--lightMain);
  border: none;
  color: gray;
  align-items: center;
}
@media screen and (max-width: 1024px) {
  .footer .up-footer {
    margin: 0%;
    text-align: center;
    display: flex;
    padding: 0rem;
    justify-content: center;
    flex-direction: column;
  }
  .footer .bot-footer {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
  }
}

.guest-nav {
  color: black;
  background-color: var(--lightSecond);
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  padding: 1rem;
  border: 1px solid var(--lightMain);
}
