  height: 300px;
  overflow-y: scroll;
  width: inherit;
  border-bottom-left-radius: 10px;
  border-bottom-right-radius: 10px;
  border: 1px solid var(--lightMain);
  border-top: none;
}
.guest-nav .search-bar .search-popup .loading {
