  align-items: center;
  flex-direction: column;
}
.guest-nav .search-bar .search-popup .searching-template .right .username {
  font-weight: bold;
}
.guest-nav .search-bar .search-popup .searching-template .right .full-name {
  color: var(--lightMain);
}
.guest-nav .search-bar input {
  border: 1px solid var(--lightMain);
  border-radius: 8px;
  text-align: center;
  width: 100%;
  padding: 0.4rem;
  font-weight: bold;
}
.guest-nav .buttons {
  text-decoration: none;
}
.guest-nav .buttons .login {
  min-width: 30%;
  width: 100%;
  border-radius: 5px;
  background-color: #12b6ec;
  color: var(--lightSecond);
  border: none;
  font-weight: bold;
  padding: 0.4rem;
  margin-right: 1rem;
}
.guest-nav .buttons .login:hover {
  background-color: darkblue;
}
.guest-nav .buttons .sign-up {
  min-width: 30%;
  padding: 0.4rem;
  border-radius: 5px;
  color: #12b6ec;
  background-color: var(--lightSecond);
  border: none;
  font-weight: bold;
  margin-left: 1rem;
}
.guest-nav .buttons .sign-up:hover {
  background-color: gray;
}
@media screen and (max-width: 415px) {
