    font-size: small;
  }
  .guest-nav .search-bar {
    width: 40%;
  }
  .guest-nav .search-bar > input {
    padding: 0.1rem;
    font-size: x-small;
  }
  .guest-nav .buttons .login {
    padding: 0;
    margin-right: 0;
    font-size: x-small;
    padding: 0.2rem;
  }
  .guest-nav .buttons .sign-up {
    font-size: x-small;
  }
}

