sources.global.scss:
$phone : 415px;
$tablet : 1024px;
$mainColor : var(--lightMain);
$secondaryColor : var(--lightSecond);
$accentColor : rgb(18, 182, 236);

body{
    --lightMain : lightgray;
    --lightSecond : white;
    --lightAccent : rgb(18, 182, 236);
    transition: 1s all;
}

body.dark{
    --lightMain : #150050;
    --lightSecond : #3F0071;
    --lightAccent : #610094;
}

body, html{
    font-family:-apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
    padding: 0;
    margin: 0;
    width: 100%;
    height: 100%;
    background-color: $mainColor;
    // transition: 1s all;
}

.themetogglebutton{
    background-color: $secondaryColor;
    border: none;
}

.themetogglebutton:hover{
    cursor: pointer;
}

.w-4{
    width: 1rem;
}

.h-4{
    height: 1rem;
}

.w-6{
    width: 1.5rem;
}

.h-6{
    height: 1.5rem;
}   

.w-8{
    width: 2rem;
}

.h-8{
    height: 2rem;
}   


.popup-box {
    // visibility: hidden;
    position: fixed;
    background-color: #00000050;
    width: 100%;
    height: 100vh;
    top: 0;
    left: 0;
    // transition: opacity 500ms;
    .box {
        // transition: all 5s ease-in-out;
        
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
        width: 33%;
        margin: 0 auto;
        height: auto;
        max-height: 70vh;
        margin-top: 15rem;
        background: #fff;
        border-radius: 1rem;
        padding: 20px;
        border: 1px solid #999;
        overflow: auto;
      }
  }


.footer{
    display: flex;
    flex-direction: column;
    // align-items: center;
    // justify-content: center;
    .up-footer{
        display: flex;
        justify-content: space-evenly;
        margin-left: 25rem;
        margin-right: 25rem;
        font-size: smaller;
        color: gray;
        div{
            a{
                text-decoration: none;
                color: gray;
            }
        }
    }

    .bot-footer{
        padding-top: 2rem;
        padding-bottom: 3rem;
        display: flex;
        justify-content: center;
        // padding-left: 25rem;
        // padding-right: 25rem;
        font-size: smaller;
        color: gray;            
        .text{
            padding-left: 1rem;
        }
    }

    .bot-footer select{
        background-color: $mainColor;
        border:none;
        color: gray;
        align-items: center;
    }

    @media screen and (max-width: $tablet) {
        // display: flex;
        // justify-content: center;
        // align-items: center;
        // width: 50%;

        .up-footer{
            margin: 0%;
            text-align: center;
            display: flex;
            padding: 0rem;
            justify-content: center;
            flex-direction: column;
        }

        .bot-footer{
            display: flex;
            justify-content: center;
            align-items: center;
            flex-direction: column;
        }
      }
    
    
}


.guest-nav{
    color: black;
    background-color: $secondaryColor;
    display: flex;
    justify-content: space-evenly;
    align-items: center;
    padding: 1rem ;
    border: 1px solid $mainColor;
    // border-radius:;

    .title{
        color: black;   
        font-family: 'Style Script', cursive;
        font-weight: bold;
        font-size: xx-large;
    }

    .search-bar{
        width: 20%;
        .search-popup{
            position: fixed;
            // z-index: 20000000;
            top: 8%;
            right: 34%;

            background-color: $secondaryColor;
            height: 300px;
            overflow-y: scroll;
            width: inherit;
            // background-color: yellow;
            border-bottom-left-radius: 10px;
            border-bottom-right-radius: 10px;
            border : 1px solid $mainColor;
            border-top: none;
            // display: none;

            .loading{
                margin-top: 45%;
                display: flex;
                justify-content: center;
                align-items: center;
                // background-color: rgb(134, 134, 24);
            }


            .searching-template{
                display: flex;
                padding: 0.5rem;
                justify-content: space-evenly;
                align-items: center;
                border-bottom: 1px solid $mainColor;
                width: 100%;

                .left{
                    // width: 50%;
                    img {
                        border-radius: 50%;
                    }
                }

                .right{
                    color: black;
                    width: 50%;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    flex-direction: column;

                    .username{
                        font-weight: bold;
                    }

                    .full-name {
                        color: $mainColor;
                    }
                }
            }
        }
    }

    .search-bar input{
        border: 1px solid $mainColor;
        border-radius: 8px;
        text-align: center;
        width: 100%;
        padding: 0.4rem;
        font-weight: bold;
    }

    .buttons{
        text-decoration: none;

        .login{
            min-width: 30%;  
            width: 100%;
            // padding-right: 1rem;
            border-radius: 5px;
            background-color: $accentColor;
            color:$secondaryColor;
            border: none;
            font-weight: bold;
            padding: 0.4rem;
            margin-right: 1rem;
        }

        .login:hover{
            background-color: darkblue;
        }

        .sign-up{
            min-width: 30%;
            padding: 0.4rem;
            border-radius: 5px;
            color: $accentColor;
            background-color: $secondaryColor;
            border:none;
            font-weight: bold;
            margin-left: 1rem;
        }

        .sign-up:hover{
            background-color: gray;
        }
    }

    @media screen and (max-width: $phone) {
        // width: 100%;
        height: 10px;
        .title{
            font-size: small;
        }

        .search-bar {
            width: 40%;
        }

        .search-bar > input{
            padding: 0.1rem;
            font-size: x-small;
        }

        .buttons{
            .login{
                // background-color: yellow;
                padding: 0;
                margin-right: 0;
                font-size: x-small;
                padding: 0.2rem;
            }

            .sign-up{
                font-size: x-small;
            }
        }
    }

}

.user-navbar{
    .user-nav-responsive{
        z-index: 100;
        width: 100%;
        position: fixed;
        display: none;
        justify-content: space-evenly;
        align-items: center;

        background-color: $secondaryColor;
        border: 1px solid $mainColor;
        height: 2.5rem;

        .title{
            color: black;
            font-family: 'Style Script', cursive;
            font-weight: bold;
            font-size: small;
        }
    
        .search-bar{
            // width: 20%;
    
            input{
                font-size: small;
                border: 1px solid $mainColor;
                border-radius: 8px;
                text-align: center;
                width: 100%;
                // padding: 0.4rem;
            }

            .search-popup{
                position: fixed;
                // z-index: 20000000;
                top: 8%;
                right: 38%;

                background-color: $secondaryColor;
                height: 300px;
                overflow-y: scroll;
                width: inherit;
                // background-color: yellow;
                border-bottom-left-radius: 10px;
                border-bottom-right-radius: 10px;
                border : 1px solid $mainColor;
                border-top: none;
                // display: none;

                .searching-template{
                    display: flex;
                    padding: 0.5rem;
                    justify-content: space-evenly;
                    align-items: center;
                    border-bottom: 1px solid $mainColor;
                    width: 100%;

                    .left{
                        // width: 50%;
                        img {
                            border-radius: 50%;
                        }
                    }

                    .right{
                        color: black;
                        width: 50%;
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        flex-direction: column;

                        .username{
                            font-weight: bold;
                        }

                        .full-name {
                            color: $mainColor;
                        }
                    }
                }
            }
    
        }

        .profile{
            img{
                border-radius: 50%;
            }
        }

        @media screen and (max-width: $tablet) {
           display: flex;
           height: 3rem;
        }

        @media screen and (max-width: $phone) {
            display: flex;
            height: 2.5rem;
         }

    }

    .user-nav{
        z-index: 100;
        width: 100%;
        background-color: $secondaryColor;
        position: fixed;
        display: flex;
        justify-content: space-evenly;
        align-items: center;
        padding: 1rem ;
        border: 1px solid $mainColor;
    
        .title{
            color: black;
            font-family: 'Style Script', cursive;
            font-weight: bold;
            font-size: xx-large;
        }
    
        .search-bar{
            padding-left: 20%;
            width: 20%;
    
            input{
                border: 1px solid $mainColor;
                border-radius: 8px;
                text-align: center;
                width: 100%;
                padding: 0.4rem;
            }

            .search-popup{
                position: fixed;
                // z-index: 20000000;
                top: 8%;
                right: 38%;
                background-color: $secondaryColor;
                height: 300px;
                overflow-y: scroll;
                width: inherit;
                // background-color: yellow;
                border-bottom-left-radius: 10px;
                border-bottom-right-radius: 10px;
                border : 1px solid $mainColor;
                border-top: none;
                // display: none;

                .loading{
                    margin-top: 45%;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    // background-color: rgb(134, 134, 24);
                }
                .searching-template{
                    display: flex;
                    padding: 0.5rem;
                    justify-content: space-evenly;
                    align-items: center;
                    border-bottom: 1px solid $mainColor;
                    width: 100%;

                    .left{
                        // width: 50%;
                        img {
                            border-radius: 50%;
                        }
                    }

                    .right{
                        color: black;
                        width: 50%;
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        flex-direction: column;

                        .username{
                            font-weight: bold;
                        }

                        .full-name {
                            color: $mainColor;
                        }
                    }
                }
            }
    
        }
    
    
    
        .menus{
            display: flex;
            justify-content: space-evenly;
            padding-left: 15%;
    
            .homess{
                padding-right: 1rem;
            }
    
            .message{
                padding-right: 1rem;
            }
    
            .pages{
                padding-right: 1rem;
            }
    
            .activities{
                padding-right: 1rem;
            }
    
            .activities:hover{
                cursor: pointer;
            }
    
            .profile{
                img{
                    border-radius: 50%;
                }
                        
            }
    
            .profile:hover{
                cursor:pointer;
            }
    
    
        }

        

        
    
        @media screen and (max-width: $tablet) {
            display: flex;
            height: 30px;
            bottom: 0%;
            .title{
                display: none;
                font-size: small;
            }
    
            .search-bar{
                display: none;
                padding: 0;

                .search-popup{
                    top: 0;
                }
            }
    
            .menus{
                padding: 0;
                width: 100%;
                padding: 1rem;

                .profile{
                    display: none;
                }
            }
            
        }

        @media screen and (max-width: $phone) {
            display: flex;
            height: 15px;
            bottom: 0%;
            .title{
                display: none;
                font-size: small;
            }
    
            .search-bar{
                display: none;
                padding: 0;
            }
    
            .menus{
                padding: 0;
                width: 100%;
                padding: 1rem;
            }
            
        }
    
    
    }
    
}

.pop-up-activities{

    .pop-container{
        background-color: $secondaryColor;
        position: fixed;
        top: 11%;
        right: 4%;
        height: 300px;
        width: 420px;
        border-radius: 10px;
        overflow-y: scroll;
        
        .template-notif{
            padding: 1rem;
            width: 90%;
            display: flex;
            justify-content: space-evenly;
            align-items: center;
            border-bottom: 1px solid $mainColor;

            .image-left{ 
                display: flex;
                img{
                    border-radius: 50%;
                }
            }

            
            .mid-content{
                margin: 0.5rem;
                width: 100%;
            }
        }

        .template-notif:hover{
            background-color: $mainColor;
            cursor: pointer;
        }

        .start-following{
            padding: 1rem;
            width: 90%;
            display: flex;
            justify-content: space-evenly;
            align-items: center;
            border-bottom: 1px solid $mainColor;

            .image{ 
                display: flex;
                img{
                    border-radius: 50%;
                }
            }

            .cont{
                width: 100%;
                margin: 0.5rem;
            }

            .button{

                .following{
                    padding: 0.3rem;
                    font-weight: bold;
                    background-color: $secondaryColor;
                    border: 1px solid gray;
                    border-radius: 4px;
                }

                .follow{
                    padding: 0.3rem;
                    font-weight: bold;
                    color: $secondaryColor;
                    background-color: $accentColor;
                    border: 1px solid gray;
                    border-radius: 4px;
                }

                button:hover{
                    cursor: pointer;
                }
            }
        }

        .start-following:hover{
            background-color: $mainColor;
            cursor: pointer;
        }
    }

    .show-all{
        border: 1px solid $mainColor;
        position: fixed;
        width: 420px;
        top: 50%;
        right: 4%;
        button{
            padding:1rem;
            width: 100%;
            border-radius: 4px;
            background-color: $secondaryColor;
            font-weight: bold;
            color: $accentColor;
            border: none;
        }

        button:hover{
            cursor: pointer;
        }

    }
}

.pop-up{
    // border: 1px solid black;
    position: fixed;
    top: 11%;
    // left : 10%;
    right : 4%;
    width: 20%;
    background-color: $secondaryColor;
    border-radius: 8px;
    box-shadow: 2px 2px #00000050;

    @media screen and (max-width: $tablet) {
        font-size: small;
        width: 20%;
        right: 10%;
        top: 5%;
    }

    @media screen and (max-width: $phone) {
        width: 30%;
        right: 0%;
        top: 5%;
    }
}


.pop-up > .pop-up-container{
    padding: 1rem;
    .pop-up-content{
        background-color: $secondaryColor;
        border-radius: 10px;
        display: flex;
        align-items: center;
        justify-content: flex-start;
        // border: 1px solid black;
        padding: 0.7rem;
        color: black;

        border: none;

        .pop-up-content-link{
            color: black;
            border: none;
            display: flex;
            align-items: center;
            align-items: center;

            svg{
                padding-right: 10px;
            }

            img{
                padding-right: 10px;
            }
        }

        .pop-up-content-link:focus{
            border: none;
            outline: none;
        }
    }

    .pop-up-content:hover{
        background-color: lightblue;
    }

    .logout{
        // border-radius: 0px;
        border-top: 2px solid $mainColor;
    }
}



